		function _hashCode(txt) {
		     var hash = 0;
		     if (txt.length == 0) return hash;
		     for (i = 0; i < txt.length; i++) {
		          char = txt.charCodeAt(i);
		          hash = ((hash<<5)-hash)+char;
		          hash = hash & hash; // Convert to 32bit integer
		     }
		     return hash;
		}
